INSERT INTO store_keyword(store_keyword_id, review, count, store_id, keyword_id) VALUES(1, "깔끔한 사골국물 칼국수의 정석 같은 한 그릇. 단촐한 고명을 올려낸 칼국수라 별거 아닌 거 같지만 두툼하면서도 미끈한 면발에 구수한 사골국물이 어우러지니 참 맛있다 . 미슐랭+지역 특수로 다소 높은 금액이 붙은 것이 눈물찔끔 포인트지만 또 요즘 물가 생각하면 그렇게 비싼 것도 아닌듯.. 만두가 맛있다 는 얘기가 많아서 만두국도 주문해봤으나 내겐 좀 달았다. 만두국보다는 칼국수가 훨씬 맛있음. 만두가 먹고 싶다면 그냥 찐만두로 곁들이는 편이 좋을 것 같다. 웨이팅이 많은데 내부가 넓고 국수라 회전율 좋아서 금방 들어갈 수 있다.", 4, 58, 1);
INSERT INTO store_keyword(store_keyword_id, review, count, store_id, keyword_id) VALUES(2, "뭔가 힙한 사람들만 방문할 수 있을 것 같은 곳이에요. 시간이 떠서 친구들이랑 아무 카페나 들어간건데 되게 사람도 많고 화려한 곳이라 놀랐어요. 디저트류 독특해서 배가 안고팠음에도 하나 시켜봤는데 약간 현대미술 같은 느낌ㅋㅋㅋ 더티 플레이팅까진,, 아니죠,, 되게 치지해서 커피랑 먹는 디저트 보다는 와인 같은거랑 먹을 술 안주 같다는 생각이 더 들었어요. 약간의 산미가 튀는 새큼한 치즈 맛이라 또 더 그렇게 느껴졌나봐요. 벽을 밀면 ㅎ화장실이 나오는게 신기했어요!", 6, 6, 2);
