INSERT INTO store_keyword(store_keyword_id, review, count, store_id, keyword_id) VALUES(746, "세번째 방문이었는데 단품 메뉴로도 괜찮네요! 크림파스타가 생각보다 훨씬 맛있어서 좋았습니다. 단품 메뉴도 종종 먹을 듯해요.", 4, 135, 346);
